package cla;

public class Practicejava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int num1 = 50;
		int num2 = 40;
		
		System.out.println(num1+"*"+num2+"="+num1*num2);
		
		int num3 =225;
		int num4 =240;
		int sum  = num3+num4;	
		int difference = num3-num4;
		int product = num3*num4;
		int quotient =num3/num4;
		int mod=num3%num4;
		
		System.out.println(num3+ "+" +num4+ "=" +(sum));
		System.out.println(num3+ "-" +num4+ "=" +(difference));
		System.out.println(num3+ "*" +num4+ "=" +(product));
		
		System.out.println(num3+ "/"  +num4+ "=" + ( quotient));
		System.out.println(num3+ "%" + num4+ "=" +(mod));
	}

}
